CREATE TABLE participants (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    name VARCHAR(255) NOT NULL,\n    email VARCHAR(255) UNIQUE NOT NULL,\n    phone VARCHAR(20),\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\nCREATE TABLE race_draw (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    race_id INT NOT NULL,\n    participant_id INT NOT NULL,\n    draw_position INT NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (race_id) REFERENCES races(id),\n    FOREIGN KEY (participant_id) REFERENCES participants(id)\n);\n\nCREATE TABLE race_schedule (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    race_id INT NOT NULL,\n    stage INT NOT NULL,\n    race_date DATE NOT NULL,\n    start_time TIME NOT NULL,\n    end_time TIME NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (race_id) REFERENCES races(id)\n);\n\nCREATE TABLE live_monitor (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    race_id INT NOT NULL,\n    participant_id INT NOT NULL,\n    current_stage INT NOT NULL,\n    status VARCHAR(50) NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (race_id) REFERENCES races(id),\n    FOREIGN KEY (participant_id) REFERENCES participants(id)\n);\n\nCREATE TABLE race_results (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    race_id INT NOT NULL,\n    participant_id INT NOT NULL,\n    position INT NOT NULL,\n    total_time TIME NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (race_id) REFERENCES races(id),\n    FOREIGN KEY (participant_id) REFERENCES participants(id)\n);\n\nCREATE TABLE next_stage (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    race_id INT NOT NULL,\n    current_stage INT NOT NULL,\n    next_stage INT NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (race_id) REFERENCES races(id)\n);\n\nCREATE TABLE admins (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    username VARCHAR(255) UNIQUE NOT NULL,\n    password VARCHAR(255) NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Inserting default admin user\nINSERT INTO admins (username, password) VALUES  \n('admin', '$2b$10$E0HpmTbJi.uZya7ICZ.sS.Lw3Rf66x8AVjT6V9WeFjTI3uq7kdW4i'); -- The password is 'admin123' hashed with bcrypt